) R

select count(1) from ( select @RNUM:=0, Q.* from ( select * from AUTOWRITE_RESERVE_MASTER where WRITER_SEQ_ID = #USER_SEQ_ID# order by WRITE_DATETIME desc ) Q where 1=1
$SEARCH_KEY$ like concat('%', #SEARCH_VALUE#, '%')
) R

select count(1) from ( select @RNUM:=0, Q.* from ( select * from AUTOWRITE_SITE where AUTOWRITE_MASTER_SEQ_ID = #AUTOWRITE_MASTER_SEQ_ID# order by TRY_DATETIME desc ) Q where 1=1
$SEARCH_KEY$ like concat('%', #SEARCH_VALUE#, '%')
) R

select count(1) from ( select @RNUM:=0, Q.* from ( select * from AUTOWRITE_LOG where AUTOWRITE_MASTER_SEQ_ID = #AUTOWRITE_MASTER_SEQ_ID# AND AUTOWRITE_SITE_SEQ_ID = #AUTOWRITE_SITE_SEQ_ID# order by TRY_DATETIME desc ) Q where 1=1
$SEARCH_KEY$ like concat('%', #SEARCH_VALUE#, '%')
) R
